import React, { useState, useEffect } from "react";
import MainLayout from "../layouts/MainLayout";
import ContactService from "../services/ContactService";
import { NavLink } from "react-router-dom";

const ShowContact = () => {
  const [contacts, setContacts] = useState([]);

  useEffect(() => {
    fetchContacts();
  }, []);

  const fetchContacts = () => {
    ContactService.getAll()
      .then((res) => {
        setContacts(res.data.data);
        console.log(res.data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };

let id = params.id;
  let [reply, setReply] = useState({});

  const saveReview = (e) => {
    e.preventDefault();
    console.log(reply);
    ContactService.addReview(id, reply)
      .then((res) => {
        fetchContact();
      })
      .catch((e) => {
        console.log(e);
      });
  };

  return (
    <MainLayout>
      <h2 className="mt-3">ùóîùóπùóπ ùó∫ùó≤ùòÄùòÄùóÆùó¥ùó≤</h2>
      <hr />
      <div className="row"></div>
      <div className="row mt-2 row-cols-lg-1 row-cols-3 g-2">
        {contacts.map((p) => (
          <ContactList key={p._id} contact={p} />
        ))}
      </div>
    </MainLayout>
  );
};

const ContactList = (props) => {
  console.log(props);
  return (
    <div className="col">
      <div className="card">
        <div className="card-body">
          <div className="card-text">
            <h6>
              Name :{" "}
              {props.contact.cname.substring(0, 1) +
                props.contact.cname
                  .substring(1, props.contact.cname.length - 1)
                  .replace(/./g, "*") +
                props.contact.cname.substring(props.contact.cname.length - 1)}
            </h6>

            <h6>
              Email :{" "}
              {props.contact.cemail.replace(
                /^(.)(.*)(.@.*)$/,
                (_, a, b, c) => a + b.replace(/./g, "*") + c
              )}
            </h6>

            <hr />
            <h6>Message : {props.contact.cmessage}</h6>
            <div className="d-flex justify-content-end">
              <NavLink
                to={"/contact/delete/" + props.contact._id}
                className="btn btn-danger"
              >
                Delete
              </NavLink>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ShowContact;
