import React, { useState, useEffect } from "react";
import MainLayout from "../layouts/MainLayout";
import MouseService from "../services/MouseService";
import { NavLink } from "react-router-dom";
import logo from "../logo.svg";

const Mouse = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    MouseService.getAll()
      .then((res) => {
        setProducts(res.data.data);
        console.log(res.data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const handleAddToCart = (productId) => {
    let cart = localStorage.getItem("cart")
      ? JSON.parse(localStorage.getItem("cart"))
      : {};

    cart[productId] = cart[productId] ? cart[productId] + 1 : 1;

    localStorage.setItem("cart", JSON.stringify(cart));
  };

  return (
    <MainLayout>
      <h2 className="mt-3">Mouse</h2>
      <hr />
      <div className="row">
        <div className="col d-flex justify-content-end">
          <NavLink to="/mouse/new" className="btn btn-success">
            Add a Product
          </NavLink>
        </div>
      </div>
      <div className="row mt-2 row-cols-lg-4 row-cols-3 g-2">
        {products.map((p) => (
          <ProductList
            key={p._id}
            product={p}
            handleAddToCart={handleAddToCart}
          />
        ))}
      </div>
    </MainLayout>
  );
};

const ProductList = (props) => {
  console.log(props);
  const { product, handleAddToCart } = props;
  return (
    <div className="col">
      <div className="card">
        <div className="card-body">
          <img src={logo} alt="" />
          <div className="card-text">
            <h5>{product.mousebrand}</h5>
            <h6>{product.mousemodel}</h6>
            <hr />
            <p>
              {product.wired_wireless} , {product.sensor} , {product.resolution}
            </p>
            <h5>à¸¿ {product.mouseprice}</h5>
            <h6>in stock : {product.mouseunit_in_stock}</h6>
            <button
              className="btn btn-success"
              onClick={() => handleAddToCart(product._id)}
            >
              Add to Cart
            </button>{" "}
            <NavLink
              to={"/mouse/" + product._id}
              className="btn btn-primary"
            >
              Learn More
            </NavLink>{" "}
            <NavLink to={"/mouse/edit/" + product._id} className="btn btn-warning">
              Edit
            </NavLink>{" "}
            <hr />
            <NavLink
              to={"/mouse/delete/" + product._id}
              className="btn btn-danger"
            >
              Delete
            </NavLink>{" "}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Mouse;
