import React, { useState } from "react";
import "./pagecss/contact.css";
import { useNavigate } from "react-router-dom";
import MainLayout from "../layouts/MainLayout";
import ContactService from "../services/ContactService";
import swal from "sweetalert";

const Contact = () => {
  const navigate = useNavigate();
  let [contact, setContact] = useState({});

  const saveContact = (e) => {
    e.preventDefault();
    ContactService.create(contact)
      .then((res) => {
        swal({
          icon: "success",
          text: "insert complete",
          title: "Result",
        });
        navigate("/contact");
      })
      .catch((e) => {
        swal({
          icon: "error",
          text: "insert failed",
          title: "Result",
        });
        console.log(e);
      });
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setContact({ ...contact, [name]: value });
  };

  return (
    <MainLayout>
      <h2 className="mt-3">ùóñùóºùóªùòÅùóÆùó∞ùòÅ ùó®ùòÄ</h2>
      <hr />
      <div className="container-contact">
        <div className="contact-box">
          <div className="left-contact" />
          <div className="right-contact">
          <form onSubmit={saveContact}>
            <h3>üì® ‡∏ï‡∏¥‡∏î‡∏ï‡πà‡∏≠‡πÄ‡∏£‡∏≤</h3>
            <input
              type="text"
              className="form-control"
              name="cname"
              id="cname"
              placeholder="Your name"
              onChange={handleInputChange}
            />
            <input
              type="text"
              className="form-control"
              name="cphone_number"
              id="cname"
              placeholder="Your name"
              onChange={handleInputChange}
            />
            <input type="text" className="field-contact" placeholder="Phone number" />
            <textarea
              placeholder="Message"
              className="field-contact"
              defaultValue={""}
            />
            <button
              className="btn-contact"
              onClick={() => {
                swal("Success!", "Message sent successfully.", "success");
                navigate("/contact");
              }}
            >
              Send
            </button>
             </form>
          </div>
        </div>
      </div>
    </MainLayout>
  );
};

export default Contact;
