import React, { useState, useEffect } from "react";
import MainLayout from "../layouts/MainLayout";
import ContactService from "../services/ContactService";
import { NavLink } from "react-router-dom";

const ShowContact = () => {
  const [contacts, setContacts] = useState([]);

  useEffect(() => {
    fetchContacts();
  }, []);

  const fetchContacts = () => {
    ContactService.getAll()
      .then((res) => {
        setContacts(res.data.data);
        console.log(res.data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  return (
    <MainLayout>
      <h2 className="mt-3">ùó†ùóºùòÇùòÄùó≤</h2>
      <hr />
      <div className="row">
        <div className="col d-flex justify-content-end">
          <NavLink to="/contact" className="btn btn-success">
            Add a Product
          </NavLink>
        </div>
      </div>
      <div className="row mt-2 row-cols-lg-4 row-cols-3 g-2">
        {contacts.map((p) => (
          <ContactList key={p._id} contact={p} />
        ))}
      </div>
    </MainLayout>
  );
};

const ContactList = (props) => {if (props.contact.type !== 'mouse') {
  return null;
}
  console.log(props);
  return (
    <div className="col">
      <div className="card">
        <div className="card-body">
          <div className="card-text">
            <h5>{props.contact.cname}</h5>
            <h6>{props.contact.cemail}</h6>
            <hr />
            <h5>‡∏ø {props.contact.mouseprice}</h5>
            <h6>in stock : {props.contact.mouseunit_in_stock}</h6>
            <NavLink
              to={"/contact/" + props.contact._id}
              className="btn btn-primary"
            >
              Learn More
            </NavLink>{" "}
            <NavLink
              to={"/contact/" + props.contact._id}
              className="btn btn-success"
            >
              Buy Now
            </NavLink>{" "}

            <hr />
            <NavLink
              to={"/contact/delete/" + props.contact._id}
              className="btn btn-danger"
            >
              Delete
            </NavLink>{" "}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ShowContact;
