import React from 'react'
import { useNavigate, useParams, useEffect } from 'react-router-dom'
import MainLayout from '../layouts/MainLayout'
import ProductService from '../services/ProductService'
import swal from 'sweetalert'

const DeleteProduct = () => {
  let navigate = useNavigate();
  let params = useParams();
  let id = params.id;
  let [product, setProduct] = useState({});

    useEffect(() => {
        fetchProduct();
    }, []);
    const fetchProduct = () => {
        ProductService.get(params.id)
          .then((res) => {
            setProduct(res.data.data);
            console.log(res);
          })
          .catch((e) => {
            console.log(e);
          });
      }

  const deleteProduct = (e) => {
    e.preventDefault();
    swal({
      title: "Are you sure?",
      text: "Once deleted, you will not be able to recover this product!",
      icon: "warning",
      buttons: true,
      dangerMode: true,
    })
    .then((willDelete) => {
      if (willDelete) {
        ProductService.deleteProduct(id)
          .then((res) => {
            swal("The product has been deleted!", {
              icon: "success",
            });
            navigate("/product");
          })
          .catch((e) => {
            console.log(e);
            swal("Delete failed!", {
              icon: "error",
            });
          });
      } else {
        swal("The product is safe!");
      }
    });
  };

  return (
    <MainLayout>
        <h2 className="mt-3">Delete product</h2>
        <hr />
        <div>
          <button className="btn btn-danger" onClick={deleteProduct}>Delete now</button>
        </div>
    </MainLayout>
  );
};

export default DeleteProduct;
