import React, { useEffect } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import ProductService from '../services/ProductService'
import swal from 'sweetalert'
import Mouse from './Mouse'

const DeleteMouse = () => {
    let navigate = useNavigate();
    let params = useParams();
    let id = params.id;
  
    const deletemouse = (currentPage) => {
        swal({
          title: "Are you sure?",
          text: "Once deleted, you will not be able to recover this product!",
          icon: "warning",
          buttons: true,
          dangerMode: true,
        })
        .then((willDelete) => {
          if (willDelete) {
            ProductService.deletemouse(id)
              .then((res) => {
                swal("The product has been deleted!", {
                  icon: "success",
                });
                navigate(<Mouse/>);
              })
              .catch((e) => {
                console.log(e);
                swal("Delete failed!", {
                  icon: "error",
                });
                navigate(<Mouse/>);
              });
            } else {
              swal("The product is safe!", {
                icon: "success",
              });
              navigate(<Mouse/>);
            }
          })
          .catch((e) => {
            console.log(e);
            swal("Cancel failed!", {
              icon: "error",
            });
            navigate(<Mouse/>);
          });
        };
      
  
  
    useEffect(() => {
        deletemouse();
    }, []);

    
  return (
    <Mouse/>
  );
};

export default DeleteMouse
