import http from '../http-common1'

const getAll = () => {
    return http.get("/mouse")
  }
  
  const get = (id) => {
    return http.get(`/mouse/${id}`)
  }
  
  const create = (product) => {
    return http.post("/mouse", product);
  }
  
  const update = (id, product) => {
    return http.put(`/mouse/${id}`, product)
  }
  
  const deleteProduct = (id) => {
    return http.delete(`/mouse/${id}`)
  }
  
  const addReview = (id, review) => {
    console.log(`/mouse/${id}`);
    console.log(review)
    return http.patch(`/mouse/${id}`, review)
  }
const MouseService = {
    getAll,
    get,
    create,
    addReview,
    update,
    deleteProduct
  }

export default MouseService
