import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import MainLayout from "../layouts/MainLayout";
import MouseService from "../services/MouseService";
import logo from "../logo.svg";

const MouseDetail = () => {
  let params = useParams();
  let [product, setProduct] = useState({});
  const [cart, setCart] = useState([]);
  
  useEffect(() => {
    fetchProduct();
  }, []);

  let id = params.id;
  let [review, setReview] = useState({});

  const saveReview = (e) => {
    e.preventDefault();
    console.log(review);
    MouseService.addReview(id, review)
      .then((res) => {
        fetchProduct();
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setReview({ ...review, [name]: value });
  };

  const fetchProduct = () => {
    MouseService.get(params.id)
      .then((res) => {
        setProduct(res.data.data);
        console.log(res);
      })
      .catch((e) => {
        console.log(e);
      });
  };

    return (
    <MainLayout>
      <h2 className="mt-3">{product.brand}</h2>
      <div className="row mt-3">
        <div className="col-md-4">
          <img src={logo} alt="" />
        </div>
        <div className="col-md-8 border p-4">
          <p>
            <label htmlFor="" className="lblStyle">
              Brand :{" "}
            </label>{" "}
            {product.mousebrand}
          </p>
          <p>
            <label htmlFor="" className="lblStyle">
              Connection :{" "}
            </label>{" "}
            {product.wired_wireless}
          </p>
          <p>
            <label htmlFor="" className="lblStyle">
              Sensor :{" "}
            </label>{" "}
            {product.sensor}
          </p>
          <p>
            <label htmlFor="" className="lblStyle">
              Resolution :{" "}
            </label>{" "}
            {product.resolution}
          </p>
          <p>
            <label htmlFor="" className="lblStyle">
              Price :{" "}
            </label>{" "}
            ฿ {product.mouseprice}
          </p>
          <p>
            <label htmlFor="" className="lblStyle">
              In Stock :{" "}
            </label>{" "}
            {product.mouseunit_in_stock}
          </p>
          <p>
            <label htmlFor="" className="lblStyle">
              Description:{" "}
            </label>{" "}
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Ab,
            expedita doloremque aliquid a hic est necessitatibus iusto mollitia,
            quasi quod modi quas ipsum repudiandae porro sint tempore totam nam
            blanditiis vel aut placeat similique, consectetur illum. Eius
            temporibus nam vero fuga illo officia reprehenderit eveniet, itaque
            enim expedita pariatur. Nulla?
          </p>
          <p className="text-center">
            <button
              className="btn btn-primary"
              onClick={() => {
                let items = JSON.parse(localStorage.getItem("cart")) || [];
                items.push(product);
                localStorage.setItem("cart", JSON.stringify(items));
              }}
            >
              Add to Cart
            </button>
          </p>
        </div>
      </div>
      <div className="row mt-2">
        <h2>Reviews of This Product</h2>
        <hr />
        <div className="col">
          <div class="container">
            <form onSubmit={saveReview}>
              <div className="mb-3 row">
                <label htmlFor="inputName" className="col-2 col-form-label">
                  Add your review
                </label>
                <div className="col-2">
                  <select
                    class="form-select"
                    name="star"
                    id="star"
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">-Select-</option>
                    {[5, 4, 3, 2, 1].map((s) => (
                      <option value={s}>{s}</option>
                    ))}
                  </select>
                </div>
                <div className="col-6">
                  <input
                    type="text"
                    className="form-control"
                    name="comment"
                    id="comment"
                    placeholder="Comment"
                    onChange={handleInputChange}
                  />
                </div>
                <div className="col-2">
                  <button type="submit" className="btn btn-primary">
                    Save
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
        {product.reviews &&
          product.reviews.map((r) => (
            <div className="alert alert-info">
              <p>Star : {"⭐".repeat(r.star)}</p>
              <p>{r.comment}</p>
            </div>
          ))}
      </div>
    </MainLayout>
  );
};

export default MouseDetail;
