import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import MainLayout from "../layouts/MainLayout";
import ContactService from "../services/ContactService";

const ContactReply = () => {
  let params = useParams();
  let [contact, setContact] = useState({});

  useEffect(() => {
    fetchContact();
  }, []);

  let id = params.id;
  let [reply, setReply] = useState({ comment: "" });

  const saveReply = (e) => {
    e.preventDefault();
    ContactService.addContactReply(id, reply)
      .then((res) => {
        fetchContact();
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setReply({ ...reply, [name]: value });
  };

  const fetchContact = () => {
    ContactService.getContactById(params.id)
      .then((res) => {
        setContact(res.data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  return (
    <MainLayout>
      <h2 className="mt-3">{contact.brand}</h2>
      <div className="row mt-3">
        <div className="col-md-8 border p-4">
          <h5>
            <label htmlFor="" className="lblStyle">
              Name :{" "}
            </label>{" "}
            {contact.cname}
          </h5>
          <hr />
          <p>
            <label htmlFor="" className="lblStyle">
              Email :{" "}
            </label>{" "}
            {contact.cemail}
          </p>
          <p>
            <label htmlFor="" className="lblStyle">
              Phone number :{" "}
            </label>{" "}
            {contact.cphone_number}
          </p>
          <p>
            <label htmlFor="" className="lblStyle">
              Message :{" "}
            </label>{" "}
            {contact.cmessage}
          </p>
        </div>
      </div>
      <div className="row mt-2">
        <h3>Reply of This Message</h3>
        <hr />
        <div className="col">
          <div className="container">
            <form onSubmit={saveReply}>
              <div className="mb-3 row">
                <label htmlFor="inputComment" className="col-2 col-form-label">
                  Add your reply
                </label>
                <div className="col-6">
                  <input
                    type="text"
                    className="form-control"
                    id="inputComment"
                    name="comment"
                    value={reply.comment}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="col-2">
                  <button type="submit" className="btn btn-primary">
                    Save
                  </button>
                </div>
              </div>
            </form>
            <div className="row mt-3">
              {contact.reply &&
                contact.reply.map((reply, index) => (
                  <div className="col-md-8 border p-4" key={index}>
                    <p>{reply.comment}</p>
                  </div>
                ))}
            </div>
          </div>
        </div>
      </div>
    </MainLayout>
  );
};

export default ContactReply;
