import React, { useState, useEffect } from "react";
import MainLayout from "../layouts/MainLayout";
import MouseService from "../services/MouseService";
import { NavLink } from "react-router-dom";
import logo from "../logo.svg";
im

const Mouse = () => {
  const [products, setProducts] = useState([]);

  const [cartItems, setCartItems] = useState([]); //NEW

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    MouseService.getAll()
      .then((res) => {
        setProducts(res.data.data);
        console.log(res.data.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  return (
    <MainLayout>
      <h2 className="mt-3">Mouse</h2>
      <hr />
      <div className="row">
        <div className="col d-flex justify-content-end">
          <NavLink to="/mouse/new" className="btn btn-success">
            Add a Product
          </NavLink>
        </div>
      </div>
      <div className="row mt-2 row-cols-lg-4 row-cols-3 g-2">
        {products.map((p) => (
          <ProductList key={p._id} product={p} />
        ))}
      </div>
    </MainLayout>
  );
};

const ProductList = (props) => {
  console.log(props);

  const addToCart = () => {
    setCartItems([...cartItems, props.product]); //NEW
  };  

  return (
    <div className="col">
      <div className="card">
        <div className="card-body">
          <img src={logo} alt="" />
          <div className="card-text">
            <h5>{props.product.mousebrand}</h5>
            <h6>{props.product.mousemodel}</h6>
            <hr />
            <p>
              {props.product.wired_wireless} , {props.product.sensor} ,{" "}
              {props.product.resolution}
            </p>
            <h5>à¸¿ {props.product.mouseprice}</h5>
            <h6>in stock : {props.product.mouseunit_in_stock}</h6>
            <NavLink
              to={"/mouse/" + props.product._id}
              className="btn btn-primary"
            >
              Learn More
            </NavLink>{" "}
            <NavLink
              to={"/mouse/" + props.product._id}
              className="btn btn-success"
            >
              Buy Now
            </NavLink>{" "}
            <NavLink
              to={"/mouse/edit/" + props.product._id}
              className="btn btn-warning"
            >
              Edit
            </NavLink>{" "}
            <hr />
            <NavLink
              to={"/mouse/delete/" + props.product._id}
              className="btn btn-danger"
            >
              Delete
            </NavLink>{" "}
            <button onClick={addToCart} className="btn btn-success">
  Add to Cart
</button>

          </div>
        </div>
      </div>
    </div>
  );
};

export default Mouse;
